$ cat t.cc
#include <stdio.h>
#include <sys/time.h>

#include <cinttypes>

#define DAY (24L*3600L)

long strtoday_tt(time_t t) {
  return (long) (t + DAY / 2) / DAY;
}

int main() {
  printf("2147440447 -> %ld\n", strtoday_tt(2147440447));
  printf("2147440448 -> %ld\n", strtoday_tt(2147440448));
  return 0;
}
